import { useEffect, useState } from "react";
import axios from "axios";

type Arquivo = {
  id: number;
  nome_arquivo: string;
  caminho: string;
  paciente_id: number;
};

function App() {
  const [arquivos, setArquivos] = useState<Arquivo[]>([]);
  const [erro, setErro] = useState("");

  useEffect(() => {
    async function buscarTodosArquivos() {
      try {
        // Supondo que existe um endpoint que lista todos os arquivos (GET /api/arquivos)
        const response = await axios.get("http://localhost:3333/api/arquivos");
        setArquivos(response.data);
      } catch (err) {
        console.error(err);
        setErro("Erro ao carregar arquivos.");
      }
    }

    buscarTodosArquivos();
  }, []);

  return (
    <div style={{ maxWidth: 800, margin: "2rem auto", padding: "1rem" }}>
      <h1>üìÅ Lista de Documentos Cadastrados</h1>

      {erro && <p style={{ color: "red" }}>{erro}</p>}

      {arquivos.length === 0 ? (
        <p>Nenhum documento encontrado.</p>
      ) : (
        <ul>
          {arquivos.map((arquivo) => (
            <li key={arquivo.id} style={{ marginBottom: "0.5rem" }}>
              üìÑ{" "}
              <a
                href={`http://localhost:3333/uploads/${arquivo.caminho}`}
                target="_blank"
                rel="noopener noreferrer"
              >
                {arquivo.nome_arquivo}
              </a>{" "}
              (Paciente #{arquivo.paciente_id})
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default App;
